mapper.select:
SELECT *
FROM COMPANY_INFO
WHERE COMPANY_ID = #{company_id} AND COMPANY_PASSWORD = #{company_password}

SELECT count(*)
FROM COMPANY_INFO
WHERE COMPANY_ID = #{id}

mapper.insert:
UPDATE EXHIBITION_INFO
SET EXHIBITION_TITLE = #{exhibition_title} , EXHIBITION_START_DATE = #{exhibition_start_date} , EXHIBITION_END_DATE = #{exhibition_end_date} , EXHIBITION_TOTAL_DATE = #{exhibition_total_date} , EXHIBITION_IMAGE = #{exhibition_image} , EXHIBITION_CONTENTS = #{exhibition_contents} , EXHIBITION_LOCATION = #{exhibition_location} , EXHIBITION_PRICE = #{exhibition_price} , EXHIBITION_FLAG = 'W'
WHERE EXHIBITION_ID = #{exhibition_id}

mapper.select:
SELECT *
FROM CUSTOMER_INFO
WHERE CUSTOMER_PASSWORD = #{company_password} AND CUSTOMER_ID =#{company_id}
